mapper.select:
select

    ,EDU_USER.SHOW_NAME as showName
	,EDU_USER.EMAIL as email 
	,EDU_USER.MOBILE as mobile 
	,EDU_USER.PIC_IMG as picImg
from EDU_QUESTIONS_COMMENT
left join EDU_USER on EDU_USER.USER_ID=EDU_QUESTIONS_COMMENT.CUS_ID


		and EDU_QUESTIONS_COMMENT.IS_BEST = #{isBest}


		and EDU_QUESTIONS_COMMENT.QUESTION_ID = #{questionId}


		and EDU_QUESTIONS_COMMENT.COMMENT_ID = #{commentId}



	order by EDU_QUESTIONS_COMMENT.ADD_TIME desc


	limit #{limitSize}

select
,EDU_USER.SHOW_NAME as showName ,EDU_USER.EMAIL as email ,EDU_USER.MOBILE as mobile ,EDU_USER.PIC_IMG as picImg from EDU_QUESTIONS_COMMENT left join EDU_USER on EDU_USER.USER_ID=EDU_QUESTIONS_COMMENT.CUS_ID where EDU_QUESTIONS_COMMENT.QUESTION_ID = #{ questionId}
and EDU_QUESTIONS_COMMENT.IS_BEST = #{ isBest}
order by EDU_QUESTIONS_COMMENT.ADD_TIME desc

select
count(1)
from EDU_QUESTIONS_COMMENT
where EDU_QUESTIONS_COMMENT.QUESTION_ID = #{  questionId}

	and EDU_QUESTIONS_COMMENT.IS_BEST = #{  isBest}

select
,EDU_USER.SHOW_NAME as showName ,EDU_USER.EMAIL as email ,EDU_USER.MOBILE as mobile ,EDU_USER.PIC_IMG as picImg ,EDU_QUESTIONS.TITLE as questionsTitle ,EDU_QUESTIONS.STATUS as questionsStatus from EDU_QUESTIONS_COMMENT left join EDU_USER on EDU_USER.USER_ID=EDU_QUESTIONS_COMMENT.CUS_ID left join EDU_QUESTIONS on EDU_QUESTIONS.ID = EDU_QUESTIONS_COMMENT.QUESTION_ID where EDU_QUESTIONS_COMMENT.QUESTION_ID!=0
and EDU_QUESTIONS_COMMENT.QUESTION_ID = #{ questionId}
and EDU_QUESTIONS.TITLE like CONCAT('%',#{ questionsTitle},'%')
AND EDU_QUESTIONS_COMMENT.ADD_TIME>#{ beginCreateTime}
AND EDU_QUESTIONS_COMMENT.ADD_TIME<#{ endCreateTime}
and EDU_QUESTIONS_COMMENT.IS_BEST = #{ isBest}
order by EDU_QUESTIONS_COMMENT.ADD_TIME desc
